import java.util.Scanner;public class Lab63 {    public static void main(String[] args) {        Scanner numb = new Scanner(System.in);        double num1, num2, num3, maxNum;        System.out.print("Введіть перше число: ");        if(numb.hasNextDouble()){            num1 = numb.nextDouble();            System.out.print("Введіть друге число:");            if(numb.hasNextDouble()){                num2 = numb.nextDouble();                System.out.print("Введіть третє число: ");                if(numb.hasNextDouble()){                    num3 = numb.nextDouble();                    maxNum = num1;                    if (num2 > maxNum){                        maxNum = num2;                    }                    if (num3 > maxNum){                        maxNum = num3;                    }                    System.out.println("Найбільше число: " + maxNum);                } else {                    System.out.println("Ви ввели не число");                }            } else {                System.out.println("Ви ввели не число");            }        }    }}