import java.util.Scanner;public class Lab64 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        System.out.print("Введіть перше ім'я: ");        String name1 = scanner.nextLine();        System.out.print("Введіть друге ім'я: ");        String name2 = scanner.nextLine();        if (name1.equalsIgnoreCase(name2)) {            System.out.println("Імена однакові");        } else {            System.out.println("Імена різні");        }    }}